import "bootstrap/dist/css/bootstrap.min.css";
import { Form, Button } from "react-bootstrap";
import { useForm } from "react-hook-form";
import "./AppLogIn.css";
import axios from "axios";

const url = "http://127.0.0.1:8080/users/sign-in";
const headers = {
  "Content-Type": "multipart/form-data"
};

function LogIn() {
  const {
    handleSubmit,
    register,
    formState: { errors },
  } = useForm({
    mode: "onChange",
  });

  const signAPIKey = async () => {
    try {
      const token = "Bearer " + localStorage.getItem("jwtToken");
      const head = {
        Authorization: token,
      };
      const resp = await axios.get("http://localhost:8080/info/apikey", {head});
      const data = resp.data;
      localStorage.setItem('x-api-key', data.key);
    }
    catch (err) {
      console.error(err)
    }
  }

  const submitForm = (e) => {
    console.log(e)
    const body = {
      "email": e.email,
      "password": e.password
    }
    //api call sign-in
    const sendSignIn = async () => {
      try {
        const response = await axios.post(url, body, { headers });
        console.log(response.data);
        const data = response.data
        // Do something with the response data
        localStorage.setItem('jwtToken', data.user.token.access_token);
      } catch (error) {
        console.error(error);
        // Handle the error
      }
    };
    // Call the sendSignIn function
    sendSignIn();
  };

  console.log(errors);
  return (
    <div className="Container">
        <Form onSubmit={handleSubmit(submitForm)} className="Form-box">
            <Form.Label className="form-label">Log-in</Form.Label>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control className="form-control"
              type="email"
              placeholder="Username"
              isInvalid={!!errors.email}
              {...register("email", { required: "email was required field." })}
            />
            <Form.Control.Feedback type="invalid">
              {errors.email?.message}
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Control className="form-control"
              type="password"
              placeholder="Password"
              isInvalid={!!errors.password}
              {...register("password", {
                required: "password was required field.",
                validate: (v) =>
                  v.length > 4 ||
                  "password length must be greater than 4 charector",
              })}
            />
            <Form.Control.Feedback type="invalid">
              {errors.password?.message}
            </Form.Control.Feedback>
          </Form.Group>

          <Button variant="primary" type="submit" className="btn-submit">
            Login
          </Button>
        </Form>
    </div>
  );
}

export default LogIn;
